// Context
import { useContext } from "react";
import { Contexto } from "../../Context/Context";

import { useState } from "react";

import CartWidget from "../../components/CartWidget/CartWidget";


export default function ItemDetail({item}) {

    const { id, title, price, category, description, image } = item;

    // console.log(useContext(Contexto));

    const { cart, Anadir } = useContext(Contexto);

    console.log(cart, Anadir)

    return (
        <>
        <article className="detailProduct">
            <div>
                <figure>
                    <img className="img-fluid" src={image} />
                </figure>
                <div>
                    <p><span>Id: </span>{id}</p>
                    <p><span>Nombre: </span>{title}</p>
                    <p><span>Precio: </span>{price}</p>
                    <p><span>Categoria: </span>{category}</p>
                    <p><span>Description: </span>{description}</p>
                </div>
            </div>
            <div>
                <button onClick={() => {
                    console.log("Agregado al carrito");
                    Anadir(
                        {
                            id: id,
                            nombre: title,
                            precio: price,
                            category: category,
                            description: description
                        }
                    );
                    console.log(cart)
                }} className="btn bg-success text-white">Agregar al carrito</button>
            </div>
        </article>
        </>
    )
}
