export default function CartWidget() {

    const { cart, setCart } = useState([]);

    useState(carrito)
    const { carrito } = useContext(Contexto);

    return (
        <>
            <img src={cart} alt="imgCarrito"/>
            <p className="m-0">Cart</p>
            <span>{carrito.length}</span>
        </>
    )
}
