import ItemList from "./ItemList";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import listaProductos from "../../data/data2.js";
import Loader from "../Loader/Loader";
import "./style.css";

export default function ItemListContainer() {

    let itemCategory = useParams();

    const [loading, setLoading] = useState(true);
    const [items, setItems] = useState([]);
    const [error, setError] = useState(false);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
        .then(res => res.json())
        .then(data => {
            setItems(data);
            setLoading(false);
        })
        .catch(data => {
            console.log(data);
            setError(true);
        })
    }, []);

    return (
        <>
            <section className="">
                <h1>Cat√°logo</h1>
                {/* <ItemList filteredCategory={itemCategory} productos={listaProductos} /> */}
                {
                    loading ? <Loader /> : <ItemList filteredCategory={itemCategory} productos={listaProductos} />
                }
            </section>
            {/* {
                loading ? <Loader /> : <Loader /> 
            } */}
        </>
    )
}
