import React, { useContext } from "react";
import { useState, createContext } from "react";
import CartWidget from "../components/CartWidget/CartWidget";

// const ContextoLocal = createContext([]);
// export const Contexto = () => useContext( ContextoLocal ) ;

export const ContextoProvider = ({children}) => {

    let cart = [];
    const [ item, setCart ] = useState(cart);

    function AddItemToCart(obj) {
        cart.push(obj)
        setCart(cart)
    }

    return (
        <ContextoLocal.Provider value={{cart, AddItemToCart}}>
            {children}
        </ContextoLocal.Provider>
    )
}
