// Components
import ItemList from "./ItemList";
import Loader from "../Loader/Loader";
import Error from "../Error/Error";
// Methods
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
// Styles
import "./style.css";

export default function ItemListContainer() {

    // Import the functions you need from the SDKs you need
    // TODO: Add SDKs for Firebase products that you want to use
    // https://firebase.google.com/docs/web/setup#available-libraries

    // Your web app's Firebase configuration
    const firebaseConfig = {
        apiKey: "AIzaSyChJBiySefkvApmYPNEhQLyUHvtHuLGRec",
        authDomain: "ecommerce-react-a9005.firebaseapp.com",
        projectId: "ecommerce-react-a9005",
        storageBucket: "ecommerce-react-a9005.appspot.com",
        messagingSenderId: "1018906915793",
        appId: "1:1018906915793:web:db132edb5eeb907456a6f0"
    };

    // Initialize Firebase
    const app = initializeApp(firebaseConfig);

    const [ loading, setLoading ] = useState(true);
    const [ items, setItems ] = useState([]);
    const [ error, setError ] = useState(false);

    const params = useParams();

    useEffect(() => {
        setTimeout(() => {
            fetch('https://fakestoreapi.com/products')
            .then(res => res.json())
            .then(data => {
                setItems(data);
                setLoading(false);
            })
            .catch(data => {
                console.log(data);
                setError(true);
            })
        }, 3000);
    }, []);

    return (
        <section className="">
            <h1>Cat√°logo</h1>
            {
                error 
                ? <Error />
                : loading 
                    ? <Loader /> 
                    : <ItemList filteredCategory={params} items={items} />
            }
        </section>
    )
}
