import {useParams} from "react-router-dom";

import listaProductos from "../data.js";

export default function ItemDetailContainer() {

    console.log(useParams());

    const {itemId} = useParams();

    const resultadoFind = listaProductos.find((producto) => producto.id == itemId);

    console.log(resultadoFind)

    return (
        <main className="productDetails">
            <article className="itemProduct d-flex justify-content-between flex-column border">
                <h1 className="text-center m-0">{resultadoFind.id}</h1>
                <img src={resultadoFind.producto}/>
                <div class="bg-secondary rounded">
                    <h4 className="bg-danger text-white p-1 rounded">{resultadoFind.nombre}</h4>
                    <p className="p-0 m-1 text-white ">{resultadoFind.description}</p>
                </div>
            </article>
        </main>
    )
}
