import React from "react";
import { useState, createContext } from "react";

export const Contexto = createContext();

let carrito = [];

export default function Context({children}) {

    function Anadir(obj) {
        carrito.push(obj)
    }

    return (
        <Contexto.Provider value={{carito, Anadir}}>
            {children}
        </Contexto.Provider>
    )
}
