// Context
import { useState, useContext } from "react";
import { Contexto } from "../../Context/Context";
// Images
import imagenCarrito from '../../imgs/carrito.svg';

export default function CartWidget({children}) {

    console.log(useContext(Contexto))

    const { cart } = useContext(Contexto);

    const [ actualItem, setActualItem ] = useState(cart.length);

    return (
        <>
            <img src={imagenCarrito} alt="imgCarrito"/>
            <p className="m-0">Cart</p>
            <span onChange={() => setActualItem(cart.length)}>{actualItem}</span>
        </>
    )
}
