import React, { useState, createContext } from "react";

export const Contexto = createContext();

let cat = [];
export default function Context({children}) {

    const [ count, setCount ] = useState([]);
    const [ filteredCart, setFilteredCart ] = useState([]);

    const AddItemToCart = (obj) => {
        cat.push(obj);
        setCount([...cat]);
    }
    const DeleteItemToCart = (pId) => {
        for ( let i = 0; i < filteredCart.length; i++ ) {
            if (filteredCart[i].id == pId) {
                filteredCart.splice(i, 1);
            }
        }
    }

    return (
        <Contexto.Provider value={{count, setCount, AddItemToCart, setFilteredCart, filteredCart, DeleteItemToCart}}>
            {children}
        </Contexto.Provider>
    )
}
