// Components
import ItemDetail from "./ItemDetail";
import Loader from "../Loader/Loader";
import Error from "../Error/Error";
// Methods
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
// Styles
import "./style.css";
// Firebase modules
import { collection, getDocs, getFirestore } from "firebase/firestore";

export default function ItemDetailContainer() {

    const [ loading, setLoading ] = useState(true);
    const [ item, setItem ] = useState([]);
    const [ error, setError ] = useState(false);

    const params = useParams();
    console.log(params)

    useEffect(() => {
        const bd = getFirestore();
        const itemsCollection = collection(bd, "items");
        getDocs(itemsCollection)
        .then((snapshot) => {
            // setItem(snapshot.docs.map((doc) => ({id: doc.id, ...doc.data()})))

            // for (let i = 0; i < snapshot.docs.data().length; i++) {
            //     if (snapshot.docs.data()[i].id == params.itemId) {
            //         setItem(snapshot.docs.data()[i]);
            //         break;
            //     }
            // }

            // snapshot.docs.map((doc) => console.log(doc.data()))

            for (let i = 0; i < snapshot.docs.length; i++) {
                if (snapshot.docs[i].id == params.itemId) {
                    console.log("found")
                }
            }
        })
        .catch(() => {
            setError(true);
        })
    }, []);

    return (
        <article className="ItemDetailContainer">
            {/* <ItemDetail id={id} title={title} price={price} category={category} description={description} image={image}/> */}
            {/* { 
                error
                ? <Error />
                : 
                    loading 
                    ? <Loader /> 
                    : <ItemDetail item={item}/> 
            } */}
        </article>
    )
}
